<?php/** Delete later * @version     4.0 +  * @package        Open Source Membership Control - com_osemsc * @subpackage    Open Source Access Control - com_osemsc * @author        Open Source Excellence (R) {@link  http://www.opensource-excellence.com} * @author        Created on 15-Nov-2010 * @license GNU/GPL http://www.gnu.org/copyleft/gpl.html * * *  This program is free software: you can redistribute it and/or modify *  it under the terms of the GNU General Public License as published by *  the Free Software Foundation, either version 3 of the License, or *  (at your option) any later version. * *  This program is distributed in the hope that it will be useful, *  but WITHOUT ANY WARRANTY; without even the implied warranty of *  MERCHANTABILITY or FITNESS FOR A PARTICULAR PURPOSE.  See the *  GNU General Public License for more details. * *  You should have received a copy of the GNU General Public License *  along with this program.  If not, see <http://www.gnu.org/licenses/>. *  @Copyright Copyright (C) 2010- Open Source Excellence (R) */defined('_JEXEC') or die("Direct Access Not Allowed");class JHTMLIcon{	function create($article, $params, $access, $attribs = array()) {		$uri = &JFactory::getURI();		$ret = $uri->toString();		$url = 'index.php?option=com_osemsc&view=article&layout=form&ret=' . base64_encode($ret);		if ($params->get('show_icons')) {			$text = JHTML::_('image.site', 'new.png', '/images/M_images/', NULL, NULL, JText::_('New'));		} else {			$text = JText::_('New') . '&nbsp;';		}		$attribs = array('title' => JText::_('New'));		return JHTML::_('link', JRoute::_($url), $text, $attribs);	}	function pdf($article, $params, $access, $attribs = array()) {		$url = 'index.php?view=article';		$url .= @$article->catslug ? '&catid=' . $article->catslug : '';		$url .= '&id=' . $article->slug . '&format=pdf';		$status = 'status=no,toolbar=no,scrollbars=yes,titlebar=no,menubar=no,resizable=yes,width=640,height=480,directories=no,location=no';		// checks template image directory for image, if non found default are loaded		if ($params->get('show_icons')) {			$text = JHTML::_('image.site', 'pdf_button.png', '/images/M_images/', NULL, NULL, JText::_('PDF'));		} else {			$text = JText::_('PDF') . '&nbsp;';		}		$attribs['title'] = JText::_('PDF');		$attribs['onclick'] = "window.open(this.href,'win2','" . $status . "'); return false;";		$attribs['rel'] = 'nofollow';		return JHTML::_('link', JRoute::_($url), $text, $attribs);	}	function email($article, $params, $access, $attribs = array()) {		$uri = &JURI::getInstance();		$base = $uri->toString(array('scheme', 'host', 'port'));		$link = $base . JRoute::_("index.php?view=article&id=" . $article->slug, false);		$url = 'index.php?option=com_mailto&tmpl=component&link=' . base64_encode($link);		$status = 'width=400,height=300,menubar=yes,resizable=yes';		if ($params->get('show_icons')) {			$text = JHTML::_('image.site', 'emailButton.png', '/images/M_images/', NULL, NULL, JText::_('Email'));		} else {			$text = '&nbsp;' . JText::_('Email');		}		$attribs['title'] = JText::_('Email');		$attribs['onclick'] = "window.open(this.href,'win2','" . $status . "'); return false;";		$output = JHTML::_('link', JRoute::_($url), $text, $attribs);		return $output;	}	function edit($article, $params, $access, $attribs = array()) {		$user = &JFactory::getUser();		$uri = &JFactory::getURI();		$ret = $uri->toString();		if ($params->get('popup')) {			return;		}		if ($article->state < 0) {			return;		}		if (!$access->canEdit && !($access->canEditOwn && $article->created_by == $user->get('id'))) {			return;		}		JHTML::_('behavior.tooltip');		$url = 'index.php?option=com_osemsc&view=article&id=' . $article->slug . '&task=edit&ret=' . base64_encode($ret);		$icon = $article->state ? 'edit.png' : 'edit_unpublished.png';		$text = JHTML::_('image.site', $icon, '/images/M_images/', NULL, NULL, JText::_('Edit'));		if ($article->state == 0) {			$overlib = JText::_('Unpublished');		} else {			$overlib = JText::_('Published');		}		$date = JHTML::_('date', $article->created);		$author = $article->created_by_alias ? $article->created_by_alias : $article->author;		$overlib .= '&lt;br /&gt;';		$overlib .= JText::_($article->groups);		$overlib .= '&lt;br /&gt;';		$overlib .= $date;		$overlib .= '&lt;br /&gt;';		$overlib .= $author;		$button = JHTML::_('link', JRoute::_($url), $text);		$output = '<span class="hasTip" title="' . JText::_('Edit Item') . ' :: ' . $overlib . '">' . $button . '</span>';		return $output;	}	function print_popup($article, $params, $access, $attribs = array()) {		$url = 'index.php?view=article';		$url .= @$article->catslug ? '&catid=' . $article->catslug : '';		$url .= '&id=' . $article->slug . '&tmpl=component&print=1&page=' . @$request->limitstart;		$status = 'status=no,toolbar=no,scrollbars=yes,titlebar=no,menubar=no,resizable=yes,width=640,height=480,directories=no,location=no';		// checks template image directory for image, if non found default are loaded		if ($params->get('show_icons')) {			$text = JHTML::_('image.site', 'printButton.png', '/images/M_images/', NULL, NULL, JText::_('Print'));		} else {			$text = JText::_('ICON_SEP') . '&nbsp;' . JText::_('Print') . '&nbsp;' . JText::_('ICON_SEP');		}		$attribs['title'] = JText::_('Print');		$attribs['onclick'] = "window.open(this.href,'win2','" . $status . "'); return false;";		return JHTML::_('link', JRoute::_($url), $text, $attribs);	}	function print_screen($article, $params, $access, $attribs = array()) {		if ($params->get('show_icons')) {			$text = JHTML::_('image.site', 'printButton.png', '/images/M_images/', NULL, NULL, JText::_('Print'));		} else {			$text = JText::_('ICON_SEP') . '&nbsp;' . JText::_('Print') . '&nbsp;' . JText::_('ICON_SEP');		}		return '<a href="#" onclick="window.print();return false;">' . $text . '</a>';	}}