$cssfile, $component) {		global $mainframe;		$tPath = 'templates/' . $mainframe->getTemplate() . '/css/' . $cssfile;		$bPath = 'components/' . $component . '/assets/css/' . $cssfile;		if (file_exists(JPATH_BASE . DS . $tPath)) {			return JURI::Base() . '/' . $tPath;		}		else if (file_exists(JPATH_BASE . DS . $bPath)) {			return JURI::Base() . '/' . $bPath;		}		else {			return false;		}	}	function _getBrowser() {		$agent = (isset($_SERVER['HTTP_USER_AGENT'])) ? strtolower($_SERVER['HTTP_USER_AGENT']) : false;		$ie_version = false;		if (eregi("msie", $agent) && !eregi("opera", $agent)) {			$val = explode(" ", stristr($agent, "msie"));			$ver = explode(".", $val[1]);			$ie_version = $ver[0];			$ie_version = ereg_replace("[^0-9,.,a-z,A-Z]", "", $ie_version);		}		return $ie_version;	}}